// set mykey3 with a value (5)
SET mykey3 5
// decrement key by by 
// mykey3 = mykey3 - 1
// work only for integers or float
DECR mykey3

// Return the value in mykey3
get mykey3

// DECR return an error other than float or integer
set mykey4 "something"
decr mykey4

// CREATE A KEY
set mykey4 6
// incr: will increment key by 1
incr mykey4
// incrby: will increment key by increment value
incrby mykey4 5
// get the final value in string
get mykey4

set mykey5 "something"
// getdel will return the value
// and immediately deletes the key
getdel mykey5

// check the keys
keys *
// you wont see mykey
